import { useState } from 'react'
import './tasks.css'

let id  = 0;
export default function Tasks(){
    const [text, setText] = useState('');
    const [tasks, setTasks] = useState([]);
    const [inputType, setInputType] = useState('checkbox');
    

    function handleChange(e){
        setText(e.target.value)
    }

    function addTasks(){
        console.log(id)
        setTasks([...tasks, {
            id: id,
            task: text,
            inputType: {inputType},
        }])
        id++;
        setText('');
    }

    function handleDelete(id){
    
        setTasks(prevTasks => {
            const updatedTasks = prevTasks.filter((task) => task.id !== id)

            return updatedTasks.map((task, index) => ({
                ...task,
                id: index
            }))
        })
    }

    function handleInputType(id){
        setTasks(prevTasks => {
           return prevTasks.map((task) => {
                if(task.id === id){
                    return {
                        ...task,
                        inputType: task.inputType === 'checkbox' ? 'text' : 'checkbox'
                    }
                }
                return task
            })
        })
    }

    function handleEdit(id, editedText){
        setTasks(prevTasks => {
            return prevTasks.map(task => {
                if(task.id === id){
                    return {
                        ...task,
                        task: editedText
                    }
                }
                return task;
            })
        })
    }


    function handleKey(e){
        if(e.key === "Enter"){
            addTasks();
        }
    }

    return(
        <div className="tasks">
            <div className="enter-tasks">
                <input
                    className='input-tasks' 
                    type="text"
                    name="text"
                    value={text}
                    placeholder="enter the tasks"
                    onChange={handleChange}
                    onKeyDown={handleKey}
                    />
                <button 
                    className="add-button"
                    onClick={addTasks}
                    >
                    +
                </button>
            </div>

            {tasks.length !== 0 ? (
        <ul className="tasks">
          {tasks.map((task) => (
            <li key={task.id}>
              <div className="entered-tasks">
                {task.inputType === 'text' ? (
                  <input
                    className="checkbox-tasks"
                    value={task.task}
                    onChange={(e) => handleEdit(task.id, e.target.value)}
                  />
                ) : (
                  <div>{task.task}</div>
                )}
                <button
                  className="edit-button"
                  onClick={() => handleInputType(task.id)}
                >
                  /
                </button>
                <button
                  className="delete-button"
                  onClick={() => handleDelete(task.id)}
                >
                  -
                </button>
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <div> </div>
      )}
    </div>
  );
}
